import ExpenseItem from './Expenseltem';
import './Expenses.css';
import Card from '../UI/Card';
import ExpensesFilter from '../NewExpense/ExpensesFilter';
import { useState } from 'react';

const Expenses = (props) => {

    const [filterDate, setFilterDate] = useState('');
    const saveExpenseDate = (dates) => {
        props.onExpenseDate(dates);
        const filteredData = props.expenses.filter(
            (expense) => expense.date.getFullYear().toString() === dates
        );
        setFilteredExpenses(filteredData);
    }


    return (
        <Card className='expenses'>
            <ExpensesFilter onDateFilter={saveExpenseDate} /> 
            {filterDate.map((expense) => (
          <ExpenseItem
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
            key={expense.id}
          />
        ))}
        </Card>
    )
}

export default Expenses;
