import ExpenseItem from './Expenseltem';
import './Expenses.css';
import Card from '../UI/Card';
import ExpensesFilter from '../NewExpense/ExpensesFilter';
import { useState } from 'react';

const Expenses = (props) => {

    const [filterDate, setFilterDate] = useState();
    const saveExpenseDate = (dates) => {
        props.onExpenseDate(dates);
        const filterData= props.item.filter((expense) => expense.date.getFullYear().toString() === dates)
    }

    const filteredExpenses = props.items.filter(expense => {
        return expense.date.getFullYear().toString() == filteredYear();
    })


    return (
        <Card className='expenses'>
            <ExpensesFilter onDateFilter={saveExpenseDate} /> 
            {filterData.map((expense) => (<ExpenseItem
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
            key={expense.id}
          />

        ))}
        </Card>
    )
}

export default Expenses;
