import React from 'react';
import ChartBar from './ChartBar';
import './Chart.css';
const Chart = (props) => {
    const dataPointValue = props.dataPoints.map((dataPoint) => dataPoint.value)
    const totalMaxinum = Math.max(...dataPointValue);
    return (
        <div className='char'>
            {props.dataPoints.map(dataPoint => <ChartBar key = {dataPoint.label} value={dataPoint.value} maxValue={totalMaxinum} label={dataPoint.label} />)}
        </div>
    )
}
export default Chart;
