import Expense from './components/Expenses/Expenses';
import NewExpense from './components/NewExpense/NewExpenses';
import React, { useState } from 'react';

const INITAL_EXPENSES = [
  {
    id: 'e1',
    title: 'Toilet Paper',
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: 'e2', title: 'New TV', amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: 'e3',
    title: 'Car Insurance',
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: 'e4',
    title: 'New Desk (Wooden)',
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

const App = () => {
  const [expenses, setExpenses] = useState(INITAL_EXPENSES);

  const addExpenseHandler = (expense) =>{
    console.log(expense);
    setExpenses((prevExpenses) => {
      console.log([expense, ...prevExpenses]);
      const updateExpenses =  [expense, ...prevExpenses];
      return updateExpenses;
    });

    // setExpenses([expense, ...expenses]);
  };

  const addExpenseDate = (dates) =>{
    console.log('In App.js');
    console.log(dates);
  }
  return (
   <div>
    <NewExpense onAddExpense  = {addExpenseHandler}/>
    <Expense items = {expenses} onExpenseDate = {addExpenseDate}/>
   </div>
  );
}

export default App;
